mapper.select:

select m_locate_Lat, m_locate_Lng
from member
where m_id = #{m_id}


select om_leader, om_state
from one_member 
where m_id = #{m_id}
and one_id = #{one_id}

mapper.update:

UPDATE and_one     
SET one_refund = '1'   
WHERE one_id = #{one_id}


UPDATE and_one     
SET one_pay = '1'   
WHERE one_id = #{one_id}
