mapper.select:
SELECT * FROM (SELECT a.c_name, b.m_id,a.c_openingdate,a.c_id,ROW_NUMBER() OVER (ORDER BY c_openingdate DESC) ROWNUMBER, (SELECT gc_name FROM common_code WHERE g_id ='019' AND a.c_category = gc_id) as gc_name,a.c_category FROM club a, c_member b WHERE a.c_id = b.c_id AND b.cm_rank = '10') WHERE rownumber between #{startIndex} AND #{endIndex} AND c_category like #{c_category}
AND c_name LIKE '%'||#{keyword}||'%'
AND m_id LIKE '%'||#{keyword}||'%'

		SELECT *
		FROM(
        SELECT c_name,c_content,c_membercnt,c_id,c_img, c_category,ROW_NUMBER() OVER (ORDER BY c_membercnt DESC) ROWNUMBER
		FROM club b
		WHERE c_category = #{c_category}
        )
   		WHERE rownumber BETWEEN #{startIndex} AND #{endIndex}

SELECT COUNT(*)
FROM club 
WHERE c_category = #{c_category}


select m_id,cm_rank
from c_member
where c_id = #{c_id}
and m_id = #{m_id}


		SELECT *
        FROM
        (
            SELECT c_name,c_content,c_hashtag,c_membercnt,gc_name,c_id,c_img,ROW_NUMBER() OVER (ORDER BY c_membercnt DESC) ROWNUMBER
            FROM common_code a INNER JOIN club b ON a.gc_id = b.c_category
            WHERE g_id = 019
            AND (gc_name like #{searchWord}
            OR c_content like #{searchWord}
            OR c_name like #{searchWord}
            OR c_hashtag like #{searchWord})
        )
        WHERE rownumber BETWEEN #{startIndex} AND #{endIndex}

		SELECT COUNT(*) 
        FROM  (
            select *
            from common_code a inner join club b on a.gc_id = b.c_category
            where g_id=019
        )
        where
        gc_name like #{searchWord}
        or c_content like #{searchWord}
        or c_name like #{searchWord}
        or c_hashtag like #{searchWord}


select ca_img
from ca_image
where ca_id = #{c_id}
