mapper.delete:
{call
	declare
	begin
		delete from club where c_id = #{c_id};
		delete from c_member where c_id = #{c_id};
		delete from c_article where c_id = #{c_id};
	end
}

mapper.update:
{call
	declare
	begin
		UPDATE c_member
		SET cm_rank = 30, cm_joindate = to_char(sysdate,'YYYY-MM-DD hh:mm')
		WHERE m_id = #{m_id}
		AND c_id = #{c_id};
		UPDATE club
		SET c_membercnt = c_membercnt + 1
		WHERE c_id = #{c_id};
	end
}

UPDATE c_member
SET cm_rank = 20
WHERE c_id = #{c_id}
AND m_id = #{m_id}

UPDATE c_member
SET cm_rank = 99
WHERE c_id = #{c_id}
AND m_id = #{m_id}

UPDATE club
SET c_membercnt = c_membercnt - 1
WHERE c_id = #{c_id}

UPDATE c_member
SET cm_rank = 30
WHERE c_id = #{c_id}
AND m_id = #{m_id}
