mapper.select:


select count(*) 
from b_member
where bm_id = #{bm_id}



select count(*) 
from b_member
where bm_email = #{bm_email}



SELECT NVL(max(bm_id),0) as bm_id
FROM b_member
WHERE bm_name = #{bm_name} 
AND bm_phoneNumber = #{bm_phoneNumber}


SELECT BM_PWD
FROM B_MEMBER
WHERE BM_ID = #{bm_id}


   			SELECT BM_EMAIL
			FROM B_MEMBER
			WHERE BM_EMAIL LIKE #{email}


   			SELECT BM_PHONENUMBER
			FROM B_MEMBER
			WHERE BM_PHONENUMBER LIKE #{phone}


   			SELECT S_PHONENUMBER
			FROM SHOP
			WHERE S_PHONENUMBER LIKE #{phone}


   			SELECT S_ID
			FROM SHOP
			WHERE S_ID LIKE #{shopId}

mapper.update:

UPDATE B_MEMBER
SET BM_EMAIL = #{bm_email}, BM_PWD = #{bm_pwd}, BM_PHONENUMBER = #{bm_phoneNumber}
WHERE BM_ID = #{bm_id}

mapper.select:

SELECT S_ID
FROM SHOP
WHERE BM_ID = #{bm_id}


     	SELECT
		(
		    SELECT DECODE(COUNT(M_ID),0,'0',SUM(SR_SCORE) / COUNT(S_ID)) AS SCORE
		    FROM S_REVIEW
		    WHERE S_ID = #{s_id}
		    AND SR_DATE < TO_CHAR(ADD_MONTHS(SYSDATE,-6))
		) AS SIX,
		(
		    SELECT DECODE(COUNT(M_ID),0,'0',SUM(SR_SCORE) / COUNT(S_ID)) AS SCORE
		    FROM S_REVIEW
		    WHERE S_ID = #{s_id}
		    AND SR_DATE < TO_CHAR(ADD_MONTHS(SYSDATE,-5))
		) AS FIVE,
		(
		    SELECT DECODE(COUNT(M_ID),0,'0',SUM(SR_SCORE) / COUNT(S_ID)) AS SCORE
		    FROM S_REVIEW
		    WHERE S_ID = #{s_id}
		    AND SR_DATE < TO_CHAR(ADD_MONTHS(SYSDATE,-4))
		) AS FOUR,
		(
		    SELECT DECODE(COUNT(M_ID),0,'0',SUM(SR_SCORE) / COUNT(S_ID)) AS SCORE
		    FROM S_REVIEW
		    WHERE S_ID = #{s_id}
		    AND SR_DATE < TO_CHAR(ADD_MONTHS(SYSDATE,-3))
		) AS THREE,
		(
		    SELECT DECODE(COUNT(M_ID),0,'0',SUM(SR_SCORE) / COUNT(S_ID)) AS SCORE
		    FROM S_REVIEW
		    WHERE S_ID = #{s_id}
		    AND SR_DATE < TO_CHAR(ADD_MONTHS(SYSDATE,-2))
		) AS TWO,
		(
		    SELECT DECODE(COUNT(M_ID),0,'0',SUM(SR_SCORE) / COUNT(S_ID)) AS SCORE
		    FROM S_REVIEW
		    WHERE S_ID = #{s_id}
		    AND SR_DATE < TO_CHAR(ADD_MONTHS(SYSDATE,-1))
		) AS ONE,
		(
		    SELECT DECODE(COUNT(M_ID),0,'0',SUM(SR_SCORE) / COUNT(S_ID)) AS SCORE
		    FROM S_REVIEW
		    WHERE S_ID = #{s_id}
		    AND SR_DATE < TO_CHAR(ADD_MONTHS(SYSDATE,0))
		) AS RECENT
		FROM DUAL
