AND M_ID = #{m_id}
AND RI_IDX = #{ri_idx}

mapper.select:

SELECT SI_IMG
FROM S_IMAGE
WHERE S_ID = #{s_id}
AND SI_IDX = #{si_idx}


SELECT RI_IMG
FROM R_IMAGE
WHERE S_ID = #{s_id}
AND M_ID = #{m_id}
AND RI_IDX = #{ri_idx}


SELECT M_IMG
FROM MEMBER
WHERE M_ID = #{m_id}

